mapper.update:
UPDATE tbl_attend
SET att_code =#{att_code}
WHERE
emp_no= #{emp_no}
and attend_date = #{attend_date}

update tbl_al
set al_approved = #{al_approved}
where emp_no = #{emp_no}
and
AL_start_DATE=TO_DATE ( #{al_start_date} ,'yy/MM/dd')

mapper.select:
select a.attend_date ,c2.code_value branch_name ,c3.code_value department_name, c.code_value rank_name,e.emp_name, a.attend_on, a.attend_off , c4.code_value att_state from tbl_attend a left join tbl_emp e on a.emp_no = e.emp_no left join tbl_org o on e.emp_no = o.emp_no left join tbl_code c on o.rank_code = c.code_category||c.code_num left join tbl_code c2 on o.branch_code = c2.code_category||c2.code_num left join tbl_code c3 on o.dept_code = c3.code_category||c3.code_num left join tbl_code c4 on a.att_code = c4.code_category||c4.code_num where to_date(a.attend_date, 'yyyy/MM/dd') = #{attend_date}
and c2.code_value = #{branch_name,jdbcType=VARCHAR}
and c3.code_value = #{department_name,jdbcType=VARCHAR}
and c.code_value = #{rank_name,jdbcType=VARCHAR}
and c4.code_value = #{att_state,jdbcType=VARCHAR}
order by a.attend_date ,c2.code_value,c3.code_value,c.code_num, e.emp_name
