mapper.insert:
DECLARE 
v_EA_NUM NUMBER :=0;

BEGIN
v_EA_NUM := seq_ea_num.nextval;


into tbl_ea (ea_num,ea_receiver, emp_no, ea_title, ea_content)
values (v_EA_NUM,#{l.ea_receiver},#{l.emp_no}, #{l.ea_title}, #{l.ea_content})



COMMIT;
END;

mapper.update:
update tbl_ea
set ea_r_statuas = #{ea_status}, ea_a_date = current_date 
where ea_num = #{ea_num} and ea_receiver = #{emp_no}
